import{_ as e,c as s,o as t,ag as n}from"./chunks/framework.lXlForvM.js";const u=JSON.parse('{"title":"Syntax Reference","description":"","frontmatter":{},"headers":[],"relativePath":"reference/syntax.md","filePath":"reference/syntax.md"}'),i={name:"reference/syntax.md"};function o(p,a,l,r,d,c){return t(),s("div",null,[...a[0]||(a[0]=[n(`<h1 id="syntax-reference" tabindex="-1">Syntax Reference <a class="header-anchor" href="#syntax-reference" aria-label="Permalink to &quot;Syntax Reference&quot;">​</a></h1><p>Complete syntax reference for YAML Manipulator rules.</p><h2 id="rule-structure" tabindex="-1">Rule Structure <a class="header-anchor" href="#rule-structure" aria-label="Permalink to &quot;Rule Structure&quot;">​</a></h2><p>A rule has two parts entered in separate fields:</p><p><strong>Condition field:</strong> What to match</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>&lt;condition&gt;</span></span></code></pre></div><p><strong>Action field:</strong> What to do</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>&lt;operation&gt; [arguments]</span></span></code></pre></div><p><strong>Important:</strong> The plugin automatically evaluates conditions and executes actions. Just enter the condition and action directly.</p><h2 id="action-syntax-hybrid-v2-0-grammar" tabindex="-1">Action Syntax: Hybrid v2.0 Grammar <a class="header-anchor" href="#action-syntax-hybrid-v2-0-grammar" aria-label="Permalink to &quot;Action Syntax: Hybrid v2.0 Grammar&quot;">​</a></h2><p><strong>Two syntax styles based on target type:</strong></p><p><strong>SCALAR operations</strong> (no FOR prefix):</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>SET field value               # Set/create field</span></span>
<span class="line"><span>DELETE field                  # Delete field</span></span>
<span class="line"><span>RENAME field TO newname       # Rename field</span></span>
<span class="line"><span>INCREMENT field number        # Add to number</span></span>
<span class="line"><span>DECREMENT field number        # Subtract from number</span></span></code></pre></div><p><strong>COLLECTION operations</strong> (FOR prefix required):</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR array APPEND value        # Add to end</span></span>
<span class="line"><span>FOR array PREPEND value       # Add to start</span></span>
<span class="line"><span>FOR array INSERT value AT index  # Insert at position</span></span>
<span class="line"><span>FOR array WHERE condition SET field value  # Update matching items</span></span>
<span class="line"><span>FOR array SORT BY field       # Sort array</span></span>
<span class="line"><span>FOR object MERGE data         # Merge objects</span></span></code></pre></div><p><strong>Key principle:</strong> <strong>SCALARS</strong> operate on single fields directly. <strong>COLLECTIONS</strong> operate on arrays/objects and need FOR to specify the target.</p><h2 id="conditions" tabindex="-1">Conditions <a class="header-anchor" href="#conditions" aria-label="Permalink to &quot;Conditions&quot;">​</a></h2><h3 id="comparison-operators" tabindex="-1">Comparison Operators <a class="header-anchor" href="#comparison-operators" aria-label="Permalink to &quot;Comparison Operators&quot;">​</a></h3><ul><li><code>=</code> - Equals</li><li><code>!=</code> - Not equals</li><li><code>&gt;</code> - Greater than</li><li><code>&lt;</code> - Less than</li><li><code>&gt;=</code> - Greater than or equal</li><li><code>&lt;=</code> - Less than or equal</li></ul><p><strong>Condition examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>status = &quot;active&quot;</span></span>
<span class="line"><span>count &gt; 10</span></span>
<span class="line"><span>date &gt;= &quot;2025-01-01&quot;</span></span></code></pre></div><h3 id="logical-operators" tabindex="-1">Logical Operators <a class="header-anchor" href="#logical-operators" aria-label="Permalink to &quot;Logical Operators&quot;">​</a></h3><ul><li><code>AND</code> - Both conditions must be true</li><li><code>OR</code> - Either condition can be true</li><li><code>NOT</code> - Negates a condition</li></ul><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>tags contains &quot;project&quot; AND status != &quot;archived&quot;</span></span>
<span class="line"><span>priority = &quot;high&quot; OR deadline &lt; NOW()</span></span>
<span class="line"><span>NOT HAS completed_date</span></span></code></pre></div><h3 id="special-operators" tabindex="-1">Special Operators <a class="header-anchor" href="#special-operators" aria-label="Permalink to &quot;Special Operators&quot;">​</a></h3><ul><li><code>contains</code> - Check if array contains value</li><li><code>HAS</code> - Check if field exists</li><li><code>IN</code> - Check if value is in list</li></ul><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>tags contains &quot;important&quot;</span></span>
<span class="line"><span>HAS custom_field</span></span>
<span class="line"><span>status IN [&quot;draft&quot;, &quot;review&quot;, &quot;published&quot;]</span></span></code></pre></div><h2 id="action-operations" tabindex="-1">Action Operations <a class="header-anchor" href="#action-operations" aria-label="Permalink to &quot;Action Operations&quot;">​</a></h2><h3 id="set-create-or-update-fields" tabindex="-1">SET - Create or Update Fields <a class="header-anchor" href="#set-create-or-update-fields" aria-label="Permalink to &quot;SET - Create or Update Fields&quot;">​</a></h3><p><strong>Purpose:</strong> Set a field value (creates if missing, updates if exists)</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>SET &lt;field&gt; &lt;value&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>SET status &quot;active&quot;</span></span>
<span class="line"><span>SET priority 1</span></span>
<span class="line"><span>SET tags [&quot;project&quot;, &quot;active&quot;]</span></span>
<span class="line"><span>SET metadata {author: &quot;John&quot;, version: 2}</span></span></code></pre></div><p><strong>Use when:</strong> You need to ensure a field has a specific value regardless of its current state.</p><h3 id="for-append-add-to-end-of-array" tabindex="-1">FOR ... APPEND - Add to End of Array <a class="header-anchor" href="#for-append-add-to-end-of-array" aria-label="Permalink to &quot;FOR ... APPEND - Add to End of Array&quot;">​</a></h3><p><strong>Purpose:</strong> Add item to end of array (with de-duplication)</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; APPEND &lt;value&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tags APPEND &quot;reviewed&quot;</span></span>
<span class="line"><span>FOR categories APPEND &quot;work&quot;</span></span></code></pre></div><p><strong>Use when:</strong> Building lists incrementally, adding to the end without duplicates.</p><h3 id="for-prepend-add-to-start-of-array" tabindex="-1">FOR ... PREPEND - Add to Start of Array <a class="header-anchor" href="#for-prepend-add-to-start-of-array" aria-label="Permalink to &quot;FOR ... PREPEND - Add to Start of Array&quot;">​</a></h3><p><strong>Purpose:</strong> Add item to start of array (with de-duplication)</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; PREPEND &lt;value&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tags PREPEND &quot;urgent&quot;</span></span>
<span class="line"><span>FOR categories PREPEND &quot;important&quot;</span></span></code></pre></div><p><strong>Use when:</strong> Adding priority items to the beginning of lists.</p><h3 id="for-remove-delete-from-lists-conditional" tabindex="-1">FOR ... REMOVE - Delete from Lists (Conditional) <a class="header-anchor" href="#for-remove-delete-from-lists-conditional" aria-label="Permalink to &quot;FOR ... REMOVE - Delete from Lists (Conditional)&quot;">​</a></h3><p><strong>Purpose:</strong> Remove specific items matching a condition</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; WHERE &lt;condition&gt; REMOVE</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tags WHERE $ = &quot;draft&quot; REMOVE</span></span>
<span class="line"><span>FOR items WHERE priority &lt; 3 REMOVE</span></span></code></pre></div><p><strong>Note:</strong> REMOVE requires a WHERE clause to specify what to remove.</p><h3 id="for-remove-all-delete-all-occurrences" tabindex="-1">FOR ... REMOVE_ALL - Delete All Occurrences <a class="header-anchor" href="#for-remove-all-delete-all-occurrences" aria-label="Permalink to &quot;FOR ... REMOVE_ALL - Delete All Occurrences&quot;">​</a></h3><p><strong>Purpose:</strong> Remove all instances of a specific value</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; REMOVE_ALL &lt;value&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tags REMOVE_ALL &quot;draft&quot;</span></span>
<span class="line"><span>FOR categories REMOVE_ALL &quot;archived&quot;</span></span></code></pre></div><p><strong>Use when:</strong> Removing a specific value regardless of how many times it appears.</p><h3 id="for-remove-any-delete-multiple-values" tabindex="-1">FOR ... REMOVE_ANY - Delete Multiple Values <a class="header-anchor" href="#for-remove-any-delete-multiple-values" aria-label="Permalink to &quot;FOR ... REMOVE_ANY - Delete Multiple Values&quot;">​</a></h3><p><strong>Purpose:</strong> Remove all instances of any value in a list</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; REMOVE_ANY [&lt;value1&gt;, &lt;value2&gt;, ...]</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tags REMOVE_ANY [&quot;draft&quot;, &quot;wip&quot;, &quot;temp&quot;]</span></span>
<span class="line"><span>FOR categories REMOVE_ANY [&quot;old&quot;, &quot;deprecated&quot;]</span></span></code></pre></div><p><strong>Use when:</strong> Cleaning up multiple unwanted values at once.</p><h3 id="delete-remove-entire-field" tabindex="-1">DELETE - Remove Entire Field <a class="header-anchor" href="#delete-remove-entire-field" aria-label="Permalink to &quot;DELETE - Remove Entire Field&quot;">​</a></h3><p><strong>Purpose:</strong> Remove field completely from front matter</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DELETE &lt;field&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DELETE old_field</span></span>
<span class="line"><span>DELETE temp_status</span></span></code></pre></div><p><strong>Use when:</strong> Cleaning up obsolete or temporary fields.</p><h3 id="rename-rename-field" tabindex="-1">RENAME - Rename Field <a class="header-anchor" href="#rename-rename-field" aria-label="Permalink to &quot;RENAME - Rename Field&quot;">​</a></h3><p><strong>Purpose:</strong> Rename a field while preserving its value across all documents</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>RENAME &lt;old_field&gt; TO &lt;new_field&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>RENAME author TO creator</span></span>
<span class="line"><span>RENAME due_date TO deadline</span></span>
<span class="line"><span>RENAME tags TO categories</span></span></code></pre></div><p><strong>Why it&#39;s atomic:</strong> Cannot be replicated with DELETE + SET because each document has unique values. RENAME preserves all values:</p><p><strong>Before (different values in different docs):</strong></p><div class="language-yaml vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Doc 1: author: &quot;Alice&quot;</span></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Doc 2: author: &quot;Bob&quot;</span></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Doc 3: author: &quot;Charlie&quot;</span></span></code></pre></div><p><strong>After <code>RENAME author TO creator</code>:</strong></p><div class="language-yaml vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">yaml</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Doc 1: creator: &quot;Alice&quot;</span></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Doc 2: creator: &quot;Bob&quot;</span></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># Doc 3: creator: &quot;Charlie&quot;</span></span></code></pre></div><p><strong>Use when:</strong> Standardizing field names across your vault while preserving all unique values.</p><h3 id="replace-pattern-substitution" tabindex="-1">REPLACE - Pattern Substitution <a class="header-anchor" href="#replace-pattern-substitution" aria-label="Permalink to &quot;REPLACE - Pattern Substitution&quot;">​</a></h3><p><strong>Purpose:</strong> Find and replace patterns in string values using regex</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>REPLACE &lt;field&gt; /&lt;regex&gt;/ &lt;replacement&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>REPLACE author /^Dr\\.?\\s+// &quot;&quot;</span></span>
<span class="line"><span>REPLACE title /\\s+/ &quot;_&quot;</span></span>
<span class="line"><span>REPLACE content /TODO: // &quot;✓ &quot;</span></span></code></pre></div><p><strong>Use when:</strong> Transforming string content with pattern matching.</p><h3 id="increment-add-to-numeric-field" tabindex="-1">INCREMENT - Add to Numeric Field <a class="header-anchor" href="#increment-add-to-numeric-field" aria-label="Permalink to &quot;INCREMENT - Add to Numeric Field&quot;">​</a></h3><p><strong>Purpose:</strong> Add to numeric field (positive or negative)</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>INCREMENT &lt;field&gt; &lt;amount&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>INCREMENT view_count 1</span></span>
<span class="line"><span>INCREMENT priority -1</span></span>
<span class="line"><span>INCREMENT score 10</span></span></code></pre></div><p><strong>Use when:</strong> Tracking counters, scores, or numeric values that change over time.</p><h3 id="decrement-subtract-from-numeric-field" tabindex="-1">DECREMENT - Subtract from Numeric Field <a class="header-anchor" href="#decrement-subtract-from-numeric-field" aria-label="Permalink to &quot;DECREMENT - Subtract from Numeric Field&quot;">​</a></h3><p><strong>Purpose:</strong> Subtract from numeric field</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DECREMENT &lt;field&gt; &lt;amount&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DECREMENT remaining_tasks 1</span></span>
<span class="line"><span>DECREMENT credits 5</span></span>
<span class="line"><span>DECREMENT attempts 1</span></span></code></pre></div><p><strong>Use when:</strong> Counting down values or tracking decreases.</p><h3 id="for-insert-insert-at-position" tabindex="-1">FOR ... INSERT - Insert at Position <a class="header-anchor" href="#for-insert-insert-at-position" aria-label="Permalink to &quot;FOR ... INSERT - Insert at Position&quot;">​</a></h3><p><strong>Purpose:</strong> Insert value at specific position in arrays or strings</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;field&gt; INSERT &lt;value&gt; AT &lt;index&gt;</span></span></code></pre></div><p><strong>Special indices:</strong></p><ul><li><code>0</code> - Insert at start (beginning)</li><li><code>-1</code> - Insert at end</li><li>Positive numbers - Insert at that position</li><li>Negative numbers - Count from end</li></ul><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># Arrays</span></span>
<span class="line"><span>FOR tags INSERT &quot;urgent&quot; AT 0        # Insert at start</span></span>
<span class="line"><span>FOR tags INSERT &quot;archived&quot; AT -1     # Insert at end</span></span>
<span class="line"><span>FOR history INSERT &quot;checkpoint&quot; AT 5 # Insert at position 5</span></span>
<span class="line"><span></span></span>
<span class="line"><span># Strings (concatenation)</span></span>
<span class="line"><span>FOR title INSERT &quot;DRAFT: &quot; AT 0      # Prepend text</span></span>
<span class="line"><span>FOR notes INSERT &quot;\\n---\\n&quot; AT -1     # Append text</span></span></code></pre></div><p><strong>Use when:</strong> Adding items to arrays or concatenating strings at any position.</p><h2 id="array-of-objects-operations" tabindex="-1">Array of Objects Operations <a class="header-anchor" href="#array-of-objects-operations" aria-label="Permalink to &quot;Array of Objects Operations&quot;">​</a></h2><p>Special operations for structured arrays like task lists.</p><h3 id="for-where-set-update-items" tabindex="-1">FOR ... WHERE ... SET - Update Items <a class="header-anchor" href="#for-where-set-update-items" aria-label="Permalink to &quot;FOR ... WHERE ... SET - Update Items&quot;">​</a></h3><p><strong>Purpose:</strong> Update fields in objects matching a condition</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; WHERE &lt;condition&gt; SET &lt;field&gt; &lt;value&gt; [, &lt;field&gt; &lt;value&gt;]</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tasks WHERE status = &quot;pending&quot; SET status &quot;active&quot;, started TODAY()</span></span>
<span class="line"><span>FOR items WHERE priority &lt; 3 SET priority 5</span></span>
<span class="line"><span>FOR entries WHERE type = &quot;draft&quot; SET type &quot;review&quot;, reviewed true</span></span></code></pre></div><p><strong>Use when:</strong> Updating specific objects within arrays based on their properties.</p><h3 id="for-where-move-reorder-items" tabindex="-1">FOR ... WHERE ... MOVE - Reorder Items <a class="header-anchor" href="#for-where-move-reorder-items" aria-label="Permalink to &quot;FOR ... WHERE ... MOVE - Reorder Items&quot;">​</a></h3><p><strong>Purpose:</strong> Move items matching condition within array</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; WHERE &lt;condition&gt; MOVE TO &lt;position&gt;</span></span></code></pre></div><p><strong>Positions:</strong> <code>START</code>, <code>END</code>, <code>AFTER &lt;condition&gt;</code>, <code>BEFORE &lt;condition&gt;</code></p><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tasks WHERE priority &gt; 7 MOVE TO START</span></span>
<span class="line"><span>FOR items WHERE status = &quot;done&quot; MOVE TO END</span></span>
<span class="line"><span>FOR tasks WHERE urgent = true MOVE TO AFTER name = &quot;Planning&quot;</span></span></code></pre></div><p><strong>Use when:</strong> Prioritizing or organizing items without full sorting.</p><h3 id="for-sort-sort-arrays" tabindex="-1">FOR ... SORT - Sort Arrays <a class="header-anchor" href="#for-sort-sort-arrays" aria-label="Permalink to &quot;FOR ... SORT - Sort Arrays&quot;">​</a></h3><p><strong>Purpose:</strong> Sort array in ascending or descending order</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; SORT [&lt;order&gt;]</span></span>
<span class="line"><span>FOR &lt;array&gt; SORT BY &lt;field&gt; [&lt;order&gt;]</span></span></code></pre></div><p><strong>Orders:</strong> <code>ASC</code> (ascending), <code>DESC</code> (descending). Default is <code>ASC</code>.</p><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tags SORT ASC</span></span>
<span class="line"><span>FOR priorities SORT DESC</span></span>
<span class="line"><span>FOR tasks SORT BY priority DESC</span></span>
<span class="line"><span>FOR entries SORT BY date ASC</span></span></code></pre></div><p><strong>Use when:</strong> Organizing simple arrays or object arrays by field values.</p><h3 id="for-move-move-by-index" tabindex="-1">FOR ... MOVE - Move by Index <a class="header-anchor" href="#for-move-move-by-index" aria-label="Permalink to &quot;FOR ... MOVE - Move by Index&quot;">​</a></h3><p><strong>Purpose:</strong> Move item from one index to another</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; MOVE FROM &lt;index&gt; TO &lt;index&gt;</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tags MOVE FROM 0 TO 5</span></span>
<span class="line"><span>FOR history MOVE FROM -1 TO 0</span></span></code></pre></div><p><strong>Use when:</strong> Manual reordering by position.</p><h3 id="for-deduplicate-remove-duplicates" tabindex="-1">FOR ... DEDUPLICATE - Remove Duplicates <a class="header-anchor" href="#for-deduplicate-remove-duplicates" aria-label="Permalink to &quot;FOR ... DEDUPLICATE - Remove Duplicates&quot;">​</a></h3><p><strong>Purpose:</strong> Remove duplicate values from array</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;array&gt; DEDUPLICATE</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tags DEDUPLICATE</span></span>
<span class="line"><span>FOR categories DEDUPLICATE</span></span></code></pre></div><p><strong>Use when:</strong> Cleaning up arrays with duplicate values.</p><h2 id="object-operations" tabindex="-1">Object Operations <a class="header-anchor" href="#object-operations" aria-label="Permalink to &quot;Object Operations&quot;">​</a></h2><h3 id="for-merge-deep-merge-objects" tabindex="-1">FOR ... MERGE - Deep Merge Objects <a class="header-anchor" href="#for-merge-deep-merge-objects" aria-label="Permalink to &quot;FOR ... MERGE - Deep Merge Objects&quot;">​</a></h3><p><strong>Purpose:</strong> Deep merge objects (preserves existing nested fields)</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;object&gt; MERGE {&lt;key&gt;: &lt;value&gt;, ...}</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR metadata MERGE {author: &quot;John&quot;, version: 2}</span></span>
<span class="line"><span>FOR config MERGE {theme: &quot;dark&quot;, showIcons: true}</span></span></code></pre></div><p><strong>Use when:</strong> Adding or updating nested object fields while preserving others.</p><h3 id="for-merge-overwrite-shallow-merge-objects" tabindex="-1">FOR ... MERGE_OVERWRITE - Shallow Merge Objects <a class="header-anchor" href="#for-merge-overwrite-shallow-merge-objects" aria-label="Permalink to &quot;FOR ... MERGE_OVERWRITE - Shallow Merge Objects&quot;">​</a></h3><p><strong>Purpose:</strong> Shallow merge objects (overwrites all fields)</p><p><strong>Syntax:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR &lt;object&gt; MERGE_OVERWRITE {&lt;key&gt;: &lt;value&gt;, ...}</span></span></code></pre></div><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR metadata MERGE_OVERWRITE {author: &quot;Jane&quot;, version: 3}</span></span>
<span class="line"><span>FOR settings MERGE_OVERWRITE {mode: &quot;advanced&quot;}</span></span></code></pre></div><p><strong>Use when:</strong> Replacing entire object contents.</p><h2 id="special-values" tabindex="-1">Special Values <a class="header-anchor" href="#special-values" aria-label="Permalink to &quot;Special Values&quot;">​</a></h2><h3 id="functions" tabindex="-1">Functions <a class="header-anchor" href="#functions" aria-label="Permalink to &quot;Functions&quot;">​</a></h3><ul><li><code>NOW()</code> - Current timestamp (ISO 8601)</li><li><code>TODAY()</code> - Today&#39;s date (YYYY-MM-DD)</li><li><code>UUID()</code> - Generate unique ID</li></ul><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>SET created_date NOW()</span></span>
<span class="line"><span>SET id UUID()</span></span>
<span class="line"><span>SET updated TODAY()</span></span></code></pre></div><h3 id="data-types" tabindex="-1">Data Types <a class="header-anchor" href="#data-types" aria-label="Permalink to &quot;Data Types&quot;">​</a></h3><ul><li><strong>Strings</strong>: <code>&quot;text&quot;</code> (use quotes)</li><li><strong>Numbers</strong>: <code>42</code>, <code>3.14</code></li><li><strong>Booleans</strong>: <code>true</code>, <code>false</code></li><li><strong>Arrays</strong>: <code>[&quot;item1&quot;, &quot;item2&quot;]</code></li><li><strong>Objects</strong>: <code>{key: &quot;value&quot;, nested: {foo: &quot;bar&quot;}}</code></li><li><strong>Dates</strong>: <code>&quot;2025-01-01&quot;</code></li></ul><h2 id="multiple-actions" tabindex="-1">Multiple Actions <a class="header-anchor" href="#multiple-actions" aria-label="Permalink to &quot;Multiple Actions&quot;">​</a></h2><p>Execute multiple actions in one rule (comma-separated):</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>SET status &quot;completed&quot;, SET completed_date NOW(), FOR tags APPEND &quot;done&quot;</span></span></code></pre></div><p>Actions execute left-to-right in order.</p><h2 id="complete-examples" tabindex="-1">Complete Examples <a class="header-anchor" href="#complete-examples" aria-label="Permalink to &quot;Complete Examples&quot;">​</a></h2><h3 id="update-draft-notes" tabindex="-1">Update Draft Notes <a class="header-anchor" href="#update-draft-notes" aria-label="Permalink to &quot;Update Draft Notes&quot;">​</a></h3><p><strong>Condition:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>status = &quot;draft&quot; AND HAS tags</span></span></code></pre></div><p><strong>Action:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>SET status &quot;ready&quot;, SET reviewed_date NOW()</span></span></code></pre></div><h3 id="clean-up-archived-notes" tabindex="-1">Clean Up Archived Notes <a class="header-anchor" href="#clean-up-archived-notes" aria-label="Permalink to &quot;Clean Up Archived Notes&quot;">​</a></h3><p><strong>Condition:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>tags contains &quot;archived&quot;</span></span></code></pre></div><p><strong>Action:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>DELETE priority, DELETE deadline, SET status &quot;archived&quot;</span></span></code></pre></div><h3 id="add-missing-metadata" tabindex="-1">Add Missing Metadata <a class="header-anchor" href="#add-missing-metadata" aria-label="Permalink to &quot;Add Missing Metadata&quot;">​</a></h3><p><strong>Condition:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>NOT HAS created_date</span></span></code></pre></div><p><strong>Action:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>SET created_date NOW(), SET status &quot;new&quot;</span></span></code></pre></div><h3 id="conditional-tagging" tabindex="-1">Conditional Tagging <a class="header-anchor" href="#conditional-tagging" aria-label="Permalink to &quot;Conditional Tagging&quot;">​</a></h3><p><strong>Condition:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>priority &gt; 5 AND NOT tags contains &quot;urgent&quot;</span></span></code></pre></div><p><strong>Action:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>FOR tags APPEND &quot;urgent&quot;</span></span></code></pre></div><h2 id="see-also" tabindex="-1">See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;See Also&quot;">​</a></h2><ul><li><a href="/obsidian-yaml-manipulator/guide/actions.html">Action Reference</a> - Complete list of all operations</li><li><a href="/obsidian-yaml-manipulator/reference/examples.html">Examples</a> - Real-world usage patterns</li><li><a href="/obsidian-yaml-manipulator/guide/file-selection.html">File Selection</a> - Detailed file selection criteria syntax</li></ul>`,209)])])}const h=e(i,[["render",o]]);export{u as __pageData,h as default};
